import React, { useState } from 'react';
import SearchBar from './SearchBar';

function Blog(props) {
  const [filteredBlog, setFilteredBlog] = useState(null);
  const [selectedTheme, setSelectedTheme] = useState('');

  const handleSearch = (searchTerm) => {
    const filteredResult = props.blogs.find((blog) =>
      blog.theme.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredBlog(filteredResult);
  };

  const handleDropdownChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedTheme(selectedValue);

    // Find the first blog that matches the selected theme
    const filteredResult = props.blogs.find(
      (blog) => selectedValue === '' || blog.theme === selectedValue
    );
    setFilteredBlog(filteredResult);
  };

  return (
    <div className="blog">
      {/* Search Bar */}
      <div className="search-bar">
        <SearchBar onSearch={handleSearch} />
      </div>

      {/* Dropdown */}
      <div className="dropdown">
        <label htmlFor="theme">Filter by Theme: </label>
        <select id="theme" onChange={handleDropdownChange} value={selectedTheme}>
          <option value="">All Themes</option>
          <option value="technology">Technology</option>
          <option value="travel">Travel</option>
          {/* Add more options based on your themes */}
        </select>
      </div>

      {/* Display filtered blog */}
      {filteredBlog && (
        <div>
          <div>{filteredBlog.imgURL}</div>
          <h1>{filteredBlog.title}</h1>
          <p>{filteredBlog.content}</p>
          <p>{filteredBlog.theme}</p>
          <p>{filteredBlog.description}</p>
        </div>
      )}
    </div>
  );
}

export default Blog;
