import React, { useState } from 'react';

function Filtered(props) {
  const [filteredBlogs, setFilteredBlogs] = useState(props.blogs);
  const [selectedTitle, setSelectedTitle] = useState('');

  const handleSearch = (searchTerm) => {
    const filteredResult = props.blogs.filter((blog) =>
      blog.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredBlogs(filteredResult);
  };

  const handleDropdownChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedTheme(selectedValue);

    // Filter blogs based on the selected theme
    const filteredResult = props.blogs.filter((blog) =>
      selectedValue === '' || blog.title === selectedValue
    );
    setFilteredBlogs(filteredResult);
  };

  return (
    <div className="blog">
      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search by theme..."
          onChange={(e) => handleSearch(e.target.value)}
        />
      </div>

      {/* Dropdown */}
      <div className="dropdown">
        <label htmlFor="theme">Filter by Theme: </label>
        <select id="theme" onChange={handleDropdownChange} value={selectedTheme}>
          <option value="">All Themes</option>
          <option value="technology">Technology</option>
          <option value="travel">Travel</option>
          {/* Add more options based on your themes */}
        </select>
      </div>

      {/* Display filtered blogs */}
      {filteredBlogs.map((blog, index) => (
        <div key={index}>
          <div>{blog.imgURL}</div>
          <h1>{blog.title}</h1>
          <p>{blog.content}</p>
          <p>{blog.theme}</p>
          <p>{blog.description}</p>
        </div>
      ))}
    </div>
  );
}

export default Filtered;
