import React,{useState} from "react";
import Footer from "./Footer";
import Header from "./Header";
import Search from "./Search";
import Blog from "./Blog";
import  CreateBlog  from "./CreateBlog";
import preblog from "./info";
import Entry from "./Entry";
import Filtered from "./Filtered";
function App(){
    const [blogs, setBlogs] = useState([]);
    function addBlog(newBlog) {
        setBlogs((prevBlogs) => {
          return [...prevBlogs, newBlog];
        });
      }

  const [filteredPosts, setFilteredPosts] = useState([]);


  const [isFilterApplied, setIsFilterApplied] = useState(false);


  const handleTitleSearch = (query) => {
    const filtered = blogs.filter((post) =>
      post.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredPosts(filtered);


    setIsFilterApplied(!!query);
  };

  const handleClearFilter = () => {
    setFilteredPosts([]);
    setIsFilterApplied(false);
  };
    return(
        <div>  
            <Header/>
            <Search onSearch={handleTitleSearch} />
            {/* <Filtered preblog={preblog} /> */}
            <Filtered posts={blogs} setPosts={setBlogs} onClearFilter={handleClearFilter} />
            {isFilterApplied ? (
        <ul>
          {filteredPosts.map((post) => (
            <li key={post.id}>
              <div>
                <h3>{post.title}</h3>
                <p>{post.content}</p>
              </div>
            </li>
          ))}
        </ul>
      ) : null}
            <CreateBlog onAdd={addBlog} />
            {blogs.map((blogs, index) => {
        return(
          <Blog
            imgURL={blogs.imgURL}
            key={index}
            id={index}
            title={blogs.title}
            content={blogs.content}  
            theme={blogs.theme}
            description={blogs.description}
          />
        );
      })}

      <dl className="dictionary">{preblog.map(Entry)}</dl>      
            <Footer />
        </div>   
    );
}

export default App;
