import React, { useState } from 'react';

const Filter = ({ posts, setPosts }) => {
  const [themeFilter, setThemeFilter] = useState('');

  const handleThemeFilterChange = (e) => {
    const theme = e.target.value;
    setThemeFilter(theme);
    // Implement filtering by theme here
    const filteredPosts = theme === '' ? posts : posts.filter(post => post.theme === theme);
    setPosts(filteredPosts);
  };

  return (
    <div>

        <span>Filter by Theme: </span>
        <select value={themeFilter} onChange={handleThemeFilterChange}>
          <option value="">All</option>
          <option value="Technology">Technology</option>
          {/* Add more theme options as needed */}
        </select>

    </div>
  );
};

export default Filter;
