//           <option value="Art">Art</option>
//           <option value="Learning">Learning</option>
//         </select>
//     </div>
//   );
<div>
      <input type="text" placeholder="Filter by Author" value={authorFilter} onChange={handleAuthorFilterChange} />

      <div className="dropdown-container">
        <span>Filter by Theme: </span>
        <button className="dropdown-button">{themeFilter || 'All'}</button>
        <div className="dropdown-content">
          {themes.map((theme) => (
            <div key={theme} className="dropdown-option" onClick={() => handleThemeFilterChange(theme)}>
              {theme || 'All'}
            </div>
          ))}
        </div>
      </div>
    </div>
  )
};

export default Filter;
