import React ,{useState} from "react";
import SearchBar from "./Search";

function Blog(props){
    // function handleClick();
    const [filteredBlogs, setFilteredBlogs] = useState(props.blogs);

  const handleSearch = (searchTerm) => {
    const filteredResult = props.blogs.filter((blog) =>
      blog.theme.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredBlogs(filteredResult);
  };

return(
    <div className="blog">
        <div>{props.imgURL}</div>
        <h1>{props.title}</h1>
        <p>{props.content}</p>
        <p>{props.theme}</p>
        <p>{props.description}</p>


        <Searchbar onSearch={handleSearch} />

      {/* Display filtered blogs */}
      {filteredBlogs.map((blog, index) => (
        <div key={index}>
          <div>{blog.imgURL}</div>
          <h1>{blog.title}</h1>
          <p>{blog.content}</p>
          <p>{blog.theme}</p>
          <p>{blog.description}</p>
    </div>
    ))}
    </div>

);
}

export default Blog;
