import React,{useState} from "react";
import Footer from "./Footer";
import Header from "./Header";
import SearchBar from "./SearchBar";
import Blog from "./Blog";
import  CreateBlog  from "./CreateBlog";
import blogs from "./info";
import Entry from "./Entry";

function createEntry(entry) {
  return (
    <Entry
      key={entry.id}
      title={entry.title}
      content={entry.content}
      theme={entry.theme}
      description={entry.description}
    />
  );

function App(){
    const [blogs, setBlogs] = useState([]);
    function addBlog(newBlog) {
        setBlogs((prevBlogs) => {
          return [...prevBlogs, newBlog];
        });
      }

      const [filteredPosts, setFilteredPosts] = useState(blogs);

      const handleSearch = (filteredPosts) => {
        const filtered = blogs.filter(
          (blogs) =>
            blogs.title.toLowerCase().includes(filteredPosts.toLowerCase()) ||
            blogs.content.toLowerCase().includes(filteredPosts.toLowerCase())
        );
        setFilteredPosts(filtered);
      };
    return(
        <div>  
            <Header />
            <SearchBar blogs={blogs} onSearch={handleSearch} />
            <CreateBlog onAdd={addBlog} />
      {blogs.map((blogItem, index) => {
        return (
          <Blog
            key={index}
            id={index}
            title={blogItem.title}
            content={blogItem.content}  
            theme={blogItem.theme}
            description={blogItem.description}      
            />
        );
      })}
      {blogs.map((blogs, index) => {
        return (
          <Blog
            key={index}
            id={index}
            title={blogs.title}
            content={blogs.content}  
            theme={blogs.theme}
            description={blogs.description}      
            />
        );
      })}
            <Footer />
        </div>   
    );
}
}
export default App;
