import React, { useState } from 'react';

const Filter = ({ posts, setPosts }) => {
//     // const [authorFilter, setAuthorFilter] = useState('');
    const [themeFilter, setThemeFilter] = useState('');
    const themes = ['', 'Travel', 'Art', 'Lifestyle','Technology'];         

    const handleAuthorFilterChange = (e) => {
      const author = e.target.value;
      // Implement filtering by author here
      setAuthorFilter(author);

      const filteredPosts = posts.filter(posts =>posts.title.toUpperCase().includes(author.toUpperCase()));
      setPosts(filteredPosts);
    };

    const handleThemeFilterChange = (theme) => {
      setThemeFilter(theme);
      // Implement filtering by theme here
      const filteredPosts = theme === '' ?posts : posts.filter(posts => posts.theme === theme);
      setPosts(filteredPosts);
    };

  return (
    <div>
      {/* <input type="text" placeholder="Filter by Author" value={authorFilter} onChange={handleAuthorFilterChange} /> */}

      <div className="dropdown-container">
        <span>Filter by Theme: </span>
        <button className="dropdown-button">{themeFilter || 'All'}</button>
        <div className="dropdown-content">
          {themes.map((theme) => (
            <div key={theme} className="dropdown-option" onClick={() => handleThemeFilterChange(theme)}>
              {theme || 'All'}
            </div>
          ))}
        </div>
      </div>
    </div>
  )
};

export default Filter;
