import React, { useState } from 'react';


function Blog(props) {
  const [filteredBlog, setFilteredBlog] = useState(null);
  const [selectedTitle, setSelectedTitle] = useState('');

  const handleSearch = (searchTerm) => {
    const filteredResult = props.blogs.find((blog) =>
      blog.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredBlog(filteredResult);
  };

  const handleDropdownChange = (event) => {
    const selectedValue = event.target.value;
    setSelectedTitle(selectedValue);

    // Find the first blog that matches the selected title
    const filteredResult = props.blogs.find(
      (blog) => selectedValue === '' || blog.title === selectedValue
    );
    setFilteredBlog(filteredResult);
  };

  return (
    <div className="blog">
      {/* Search Bar */}
      {/* <div className="search-bar">
        <SearchBar onSearch={handleSearch} />
      </div> */}

      {/* Dropdown */}
      <div className="dropdown">
        <label htmlFor="title">Filter by Title: </label>
        <select id="title" onChange={handleDropdownChange} value={selectedTitle}>
          <option value="">All Titles</option>
          <option value="">Blog 1</option>
          <option value="Blog 2">Blog 2</option>
          {/* Add more options based on your blog titles */}
        </select>
      </div>

      {/* Display filtered blog */}
      {filteredBlog && (
        <div>
          <div>{filteredBlog.imgURL}</div>
          <h1>{filteredBlog.title}</h1>
          <p>{filteredBlog.content}</p>
          <p>{filteredBlog.theme}</p>
          <p>{filteredBlog.description}</p>
        </div>
      )}
    </div>
  );
}

export default Blog;
